package Globals;use base 'Exporter';our @EXPORT = qw(@todelete $not_beginning $day_tolerance compare_dates get_date_from_file undump_bz2 dump_bz2);our @todelete = qw(strong b span a);our $not_beginning="[Pp][Rr][aA][hH][aA]";use Data::Dumper;use IO::Uncompress::Bunzip2;use IO::Compress::Bzip2;sub dump_bz2 {	my $path = shift;	my $ref = shift;		my $z = IO::Compress::Bzip2->new ($path);		print $z Dumper($ref);		close($z);}sub undump_bz2 {	my $path = shift;	if (!-e $path) {		return undef;	}	my $z = IO::Uncompress::Bunzip2->new($path);		my $dumped = join ("", <$z>);	close($z);	my $VAR1;	eval($dumped);		return $VAR1;}1;